import React, { useState, useEffect } from 'react';
import {
  Typography, Button, Dialog, DialogTitle, DialogContent,
  DialogActions, TextField, Box, Alert, Select, MenuItem, FormControl,
  InputLabel, Grid, Card, CardContent, IconButton, Chip
} from '@mui/material';
import { CheckCircle, Cancel, Delete } from '@mui/icons-material';
import { api } from '../services/api';
import { useAuth } from '../hooks/useAuth';
import { Order, Product } from '../types';

const Orders: React.FC = () => {
  const { user } = useAuth();
  const [orders, setOrders] = useState<Order[]>([]);
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [createDialog, setCreateDialog] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, []);

  const fetchOrders = async () => {
    try {
      if (user) {
        const endpoint = user.role === 'ADMIN' ? '/orders' : `/orders/user/${user.id}`;
        const response = await api.get(endpoint);
        setOrders(response.data.content || response.data);
      }
    } catch (err) {
      setError('Error loading orders');
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products?size=100');
      setProducts(response.data.content);
    } catch (err) {
      console.error('Error loading products');
    }
  };

  const handleCreateOrder = async () => {
    if (!selectedProduct || !user) return;

    try {
      await api.post(`/orders?userId=${user.id}`, {
        items: [{
          productId: Number(selectedProduct),
          quantity: quantity
        }]
      });
      setCreateDialog(false);
      setSelectedProduct('');
      setQuantity(1);
      setSuccess('Orden creada exitosamente');
      setError('');
      fetchOrders();
    } catch (err: any) {
      const errorMessage = err.response?.data?.message || err.message || 'Error creating order';
      setError(errorMessage);
      setSuccess('');
    }
  };

  const handleRandomOrder = async () => {
    if (!user) return;

    try {
      await api.post(`/orders/random?userId=${user.id}`);
      setSuccess('Orden aleatoria creada exitosamente');
      setError('');
      fetchOrders();
    } catch (err: any) {
      const errorMessage = err.response?.data?.message || err.message || 'Error creating random order';
      setError(errorMessage);
      setSuccess('');
    }
  };

  const handleOrderAction = async (orderId: number, action: 'APPROVED' | 'REJECTED' | 'DELETED') => {
    if (!user) return;

    try {
      await api.put(`/orders/${orderId}/status?status=${action}&userId=${user.id}`);
      setSuccess(`Orden ${action.toLowerCase()} exitosamente`);
      setError('');
      fetchOrders();
    } catch (err: any) {
      setError('Error updating order status');
      setSuccess('');
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'APPROVED': return '#e8f5e8';
      case 'REJECTED': return '#ffeaea';
      case 'PENDING': return '#fff8e1';
      default: return '#f5f5f5';
    }
  };

  const getStatusChipColor = (status: string): 'success' | 'error' | 'warning' | 'default' => {
    switch (status) {
      case 'APPROVED': return 'success';
      case 'REJECTED': return 'error';
      case 'PENDING': return 'warning';
      default: return 'default';
    }
  };

  if (loading) return <Typography>Loading...</Typography>;

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Gestión de Órdenes</Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

      <Box sx={{ mb: 3 }}>
        <Button 
          variant="contained" 
          onClick={() => setCreateDialog(true)}
          sx={{ mr: 2 }}
        >
          Crear Orden
        </Button>
        <Button 
          variant="outlined" 
          onClick={handleRandomOrder}
        >
          Crear Orden Aleatoria
        </Button>
      </Box>

      <Grid container spacing={3}>
        {orders.map((order) => (
          <Grid item xs={12} md={6} key={order.id}>
            <Card sx={{ backgroundColor: getStatusColor(order.status) }}>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                  <Typography variant="h6">Orden #{order.id}</Typography>
                  <Chip 
                    label={order.status} 
                    color={getStatusChipColor(order.status)}
                    size="small"
                  />
                </Box>

                <Typography color="textSecondary">
                  Fecha: {new Date(order.orderDate).toLocaleDateString()}
                </Typography>
                <Typography variant="body1" sx={{ mt: 1 }}>
                  Total: ${order.totalAmount}
                </Typography>
                {order.discountApplied > 0 && (
                  <Typography color="success.main">
                    Descuento: -${order.discountApplied}
                  </Typography>
                )}

                {user?.role === 'ADMIN' && (
                  <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                    {order.status === 'PENDING' && (
                      <>
                        <IconButton 
                          size="small" 
                          color="success"
                          onClick={() => handleOrderAction(order.id, 'APPROVED')}
                          title="Aprobar"
                        >
                          <CheckCircle />
                        </IconButton>
                        <IconButton 
                          size="small" 
                          color="error"
                          onClick={() => handleOrderAction(order.id, 'REJECTED')}
                          title="Rechazar"
                        >
                          <Cancel />
                        </IconButton>
                      </>
                    )}
                    {(order.status === 'PENDING' || order.status === 'APPROVED' || order.status === 'REJECTED') && (
                      <IconButton 
                        size="small" 
                        color="error"
                        onClick={() => handleOrderAction(order.id, 'DELETED')}
                        title="Eliminar"
                      >
                        <Delete />
                      </IconButton>
                    )}
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={createDialog} onClose={() => setCreateDialog(false)}>
        <DialogTitle>Crear Nueva Orden</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Producto</InputLabel>
            <Select
              value={selectedProduct}
              onChange={(e) => setSelectedProduct(e.target.value)}
            >
              {products.map((product) => (
                <MenuItem key={product.id} value={product.id}>
                  {product.name} - ${product.price} (Stock: {product.stock})
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            label="Cantidad"
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            fullWidth
            sx={{ mt: 2 }}
            inputProps={{ min: 1 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialog(false)}>Cancelar</Button>
          <Button onClick={handleCreateOrder} variant="contained">Crear Orden</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Orders;
