import React, { useState, useEffect } from 'react';
import {
  Typography, Box, Alert, Table, TableBody,
  TableCell, TableContainer, TableHead, TableRow, Paper,
  Grid, Card, CardContent
} from '@mui/material';
import { api } from '../services/api';
import { useAuth } from '../hooks/useAuth';

interface TopProduct {
  productName: string;
  totalQuantitySold: number;
}

interface TopCustomer {
  customerName: string;
  totalOrders: number;
}

interface ActiveProduct {
  id: number;
  name: string;
  category: string;
  price: number;
  stock: number;
}

const Reports: React.FC = () => {
  const { user } = useAuth();
  const [topProducts, setTopProducts] = useState<TopProduct[]>([]);
  const [topCustomers, setTopCustomers] = useState<TopCustomer[]>([]);
  const [activeProducts, setActiveProducts] = useState<ActiveProduct[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (user?.role === 'ADMIN') {
      fetchReports();
    }
  }, [user]);

  if (user?.role !== 'ADMIN') {
    return (
      <Box>
        <Alert severity="error">
          No tienes permisos para acceder a esta secci칩n.
        </Alert>
      </Box>
    );
  }

  const fetchReports = async () => {
    try {
      const [productsRes, customersRes, activeRes] = await Promise.all([
        api.get('/products/top-selling'),
        api.get('/orders/frequent-customers'),
        api.get('/products/active')
      ]);

      setTopProducts(productsRes.data);
      setTopCustomers(customersRes.data);
      setActiveProducts(activeRes.data);
    } catch (err) {
      setError('Error loading reports');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <Typography>Loading...</Typography>;

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Reportes del Sistema</Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <Grid container spacing={3}>
        {/* Top 5 Productos M치s Vendidos */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                游늳 Top 5 Productos M치s Vendidos
              </Typography>
              <TableContainer>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell>Producto</TableCell>
                      <TableCell align="right">Cantidad Vendida</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {topProducts.map((product, index) => (
                      <TableRow key={index}>
                        <TableCell>{product.productName}</TableCell>
                        <TableCell align="right">{product.totalQuantitySold}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Top 5 Clientes Frecuentes */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                游논 Top 5 Clientes Frecuentes
              </Typography>
              <TableContainer>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell>Cliente</TableCell>
                      <TableCell align="right">칍rdenes Aprobadas</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {topCustomers.map((customer, index) => (
                      <TableRow key={index}>
                        <TableCell>{customer.customerName}</TableCell>
                        <TableCell align="right">{customer.totalOrders}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Productos Activos */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                游늵 Productos Activos ({activeProducts.length} productos)
              </Typography>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Nombre</TableCell>
                      <TableCell>Categor칤a</TableCell>
                      <TableCell align="right">Precio</TableCell>
                      <TableCell align="right">Stock</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {activeProducts.map((product) => (
                      <TableRow key={product.id}>
                        <TableCell>{product.name}</TableCell>
                        <TableCell>{product.category}</TableCell>
                        <TableCell align="right">${product.price}</TableCell>
                        <TableCell align="right">
                          <Box color={product.stock < 10 ? 'error.main' : 'text.primary'}>
                            {product.stock}
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Reports;
