import React, { useState, useEffect } from 'react';
import {
  Typography, Box, Alert, Table, TableBody,
  TableCell, TableContainer, TableHead, TableRow, Paper,
  Grid, Card, CardContent, Avatar, Fade, Chip
} from '@mui/material';
import { Assessment, TrendingUp, People, Inventory, Star, EmojiEvents } from '@mui/icons-material';
import { api } from '../services/api';
import { useAuth } from '../hooks/useAuth';

type TopProduct = [string, number]; // [productName, totalQuantitySold]
type TopCustomer = [string, number]; // [customerName, totalOrders]

interface ActiveProduct {
  id: number;
  name: string;
  category: string;
  price: number;
  stock: number;
}

const Reports: React.FC = () => {
  const { user } = useAuth();
  const [topProducts, setTopProducts] = useState<TopProduct[]>([]);
  const [topCustomers, setTopCustomers] = useState<TopCustomer[]>([]);
  const [activeProducts, setActiveProducts] = useState<ActiveProduct[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (user?.role === 'ADMIN') {
      fetchReports();
    }
  }, [user]);

  if (user?.role !== 'ADMIN') {
    return (
      <Box>
        <Alert severity="error">
          No tienes permisos para acceder a esta sección.
        </Alert>
      </Box>
    );
  }

  const fetchReports = async () => {
    try {
      const [productsRes, customersRes, activeRes] = await Promise.all([
        api.get('/products/top-selling'),
        api.get('/orders/frequent-customers'),
        api.get('/products/active')
      ]);

      setTopProducts(productsRes.data);
      setTopCustomers(customersRes.data);
      setActiveProducts(activeRes.data);
    } catch (err) {
      setError('Error loading reports');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <Typography>Loading...</Typography>;

  return (
    <Box sx={{ bgcolor: '#f8fafc', minHeight: '100vh', p: 3 }}>
      <Paper 
        sx={{ 
          p: 4, 
          mb: 4, 
          borderRadius: 3,
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white'
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Avatar sx={{ bgcolor: 'rgba(255,255,255,0.2)', width: 56, height: 56 }}>
            <Assessment sx={{ fontSize: 28 }} />
          </Avatar>
          <Typography variant="h4" sx={{ fontWeight: 700 }}>
            Reportes del Sistema
          </Typography>
        </Box>
      </Paper>

      {error && (
        <Fade in>
          <Alert 
            severity="error" 
            sx={{ 
              mb: 3, 
              borderRadius: 2,
              boxShadow: '0 4px 12px rgba(244, 67, 54, 0.15)'
            }}
          >
            {error}
          </Alert>
        </Fade>
      )}

      <Grid container spacing={3}>
        {/* Top 5 Productos Más Vendidos */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                📈 Top 5 Productos Más Vendidos
              </Typography>
              <TableContainer>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell>Producto</TableCell>
                      <TableCell align="right">Cantidad Vendida</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {topProducts.map((product, index) => (
                      <TableRow key={index}>
                        <TableCell>{product[0]}</TableCell>
                        <TableCell align="right">{product[1]}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Top 5 Clientes Frecuentes */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                👥 Top 5 Clientes Frecuentes
              </Typography>
              <TableContainer>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell>Cliente</TableCell>
                      <TableCell align="right">Órdenes Aprobadas</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {topCustomers.map((customer, index) => (
                      <TableRow key={index}>
                        <TableCell>{customer[0]}</TableCell>
                        <TableCell align="right">{customer[1]}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Productos Activos */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                📊 Productos Activos ({activeProducts.length} productos)
              </Typography>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Nombre</TableCell>
                      <TableCell>Categoría</TableCell>
                      <TableCell align="right">Precio</TableCell>
                      <TableCell align="right">Stock</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {activeProducts.map((product) => (
                      <TableRow key={product.id}>
                        <TableCell>{product.name}</TableCell>
                        <TableCell>{product.category}</TableCell>
                        <TableCell align="right">${product.price}</TableCell>
                        <TableCell align="right">
                          <Box color={product.stock < 10 ? 'error.main' : 'text.primary'}>
                            {product.stock}
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Reports;
