import React, { useState, useEffect } from 'react';
import {
  Container, Typography, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Button, Dialog, DialogTitle, DialogContent,
  DialogActions, TextField, Box, Alert, Select, MenuItem, FormControl,
  InputLabel, Grid, Card, CardContent
} from '@mui/material';
import { api } from '../services/api';
import { useAuth } from '../hooks/useAuth';

interface Product {
  id: number;
  name: string;
  price: number;
  stock: number;
}

interface Order {
  id: number;
  userId: number;
  totalAmount: number;
  discountApplied: number;
  orderDate: string;
  status: string;
  items: OrderItem[];
}

interface OrderItem {
  id: number;
  productId: number;
  quantity: number;
  unitPrice: number;
}

const Orders: React.FC = () => {
  const { user } = useAuth();
  const [orders, setOrders] = useState<Order[]>([]);
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [createDialog, setCreateDialog] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, []);

  const fetchOrders = async () => {
    try {
      if (user) {
        const response = await api.get(`/orders/user/${user.id}`);
        setOrders(response.data.content || response.data);
      }
    } catch (err) {
      setError('Error loading orders');
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products?size=100');
      setProducts(response.data.content);
    } catch (err) {
      console.error('Error loading products');
    }
  };

  const handleCreateOrder = async () => {
    if (!selectedProduct || !user) return;

    try {
      await api.post(`/orders?userId=${user.id}`, {
        items: [{
          productId: Number(selectedProduct),
          quantity: quantity
        }]
      });
      setCreateDialog(false);
      setSelectedProduct('');
      setQuantity(1);
      fetchOrders();
    } catch (err) {
      setError('Error creating order');
    }
  };

  const handleRandomOrder = async () => {
    if (!user) return;

    try {
      await api.post(`/orders/random?userId=${user.id}`);
      fetchOrders();
    } catch (err) {
      setError('Error creating random order');
    }
  };

  if (loading) return <Typography>Loading...</Typography>;

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Orders Management</Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <Box sx={{ mb: 3 }}>
        <Button 
          variant="contained" 
          onClick={() => setCreateDialog(true)}
          sx={{ mr: 2 }}
        >
          Create Order
        </Button>
        <Button 
          variant="outlined" 
          onClick={handleRandomOrder}
        >
          Create Random Order
        </Button>
      </Box>

      <Grid container spacing={3}>
        {orders.map((order) => (
          <Grid item xs={12} md={6} key={order.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">Order #{order.id}</Typography>
                <Typography color="textSecondary">
                  Date: {new Date(order.orderDate).toLocaleDateString()}
                </Typography>
                <Typography color="textSecondary">
                  Status: {order.status}
                </Typography>
                <Typography variant="body1" sx={{ mt: 1 }}>
                  Total: ${order.totalAmount}
                </Typography>
                {order.discountApplied > 0 && (
                  <Typography color="success.main">
                    Discount: -${order.discountApplied}
                  </Typography>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={createDialog} onClose={() => setCreateDialog(false)}>
        <DialogTitle>Create New Order</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Product</InputLabel>
            <Select
              value={selectedProduct}
              onChange={(e) => setSelectedProduct(e.target.value)}
            >
              {products.map((product) => (
                <MenuItem key={product.id} value={product.id}>
                  {product.name} - ${product.price} (Stock: {product.stock})
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            label="Quantity"
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            fullWidth
            sx={{ mt: 2 }}
            inputProps={{ min: 1 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateOrder} variant="contained">Create Order</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Orders;
